import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
// import Jumbotron from 'react-bootstrap/Jumbotron';
import './BestBooks.css';
import { withAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
// import BookItem from './BookItem';
import Modelbook from './Modelbook';
import Button from '@restart/ui/esm/Button';

import Card from 'react-bootstrap/Card'

class BestBooks extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      Books: [],
      showModel:false

    }
  }
  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;
    axios.get(`http://localhost:3010/Book?email=${email}`)
      .then(result => {
        this.setState({
          Books: result.data
        })
      }).catch(err => {
        console.log('error');
      })

  }
//function addBook
  addBook = (event) => {
    event.preventDefault();
    const { user } = this.props.auth0;
    const email = user.email;
    const obj = {
      title: event.target.title.value,
      description: event.target.description.value,
      email:email,
      status: event.target.status.value

    }
    axios
    .post(`http://localhost:3010/addBook`, obj)
      .then(result => {
        this.setState({
        Books: result.data
        })
      }).catch(err => {
        console.log('Error on adding Book');
      })
  }


  //function handelModel

  handelShowModel=()=>{
    this.setState({
      showModel:true
    })
  }

  // function deleteBook
  deleteBook = (id) => {

    const { user } = this.props.auth0;
    const email = user.email;
    axios
    .delete(`http://localhost:3010/deleteBook/${id}?email=${email}`)
      .then(result => {
        this.setState({
        Books: result.data
        })
      }).catch(err => {
        console.log('Error on deleting Book');
      })
  }


  render() {
    return (
      <>
        <h1>My Favorite Books</h1>
        <p>
          This is a collection of my favorite books
        </p>
      <Button onClick={this.handelShowModel}> Add  </Button>
      <div>    

        {this.state.Books.map(item=>
             <Card style={{ width: '18rem' }}>
             <Card.Body>
               <Card.Title> {item.title}</Card.Title>
               <Card.Text>
               {item.description}
               {item.email}

               {item.status}
               </Card.Text>

             </Card.Body>
             <Button variant="primary" onClick={()=>this.deleteBook(item._id)}> Delete</Button>
           </Card>
          )}</div>






       {/* <form onSubmit={this.addBook}>
          <fieldset>
            <legend>Add Cat:</legend>
            <input type='text' name='title' placeholder='title' />
            <input type='text' name='description' placeholder='description' />
            <input type='text' name='status' placeholder='status' />
            <button type='submit'> add</button>
          </fieldset>
        </form> */}
        {/* <BookItem 
       Book={this.state.Books}
       deleteBook={this.deleteBook}
       /> */}

       <Modelbook show={this.state.showModel}
       handelShowModel={this.handelShowModel}
       addBook={this.addBook}/>
      </>
    )
  }
}

export default withAuth0(BestBooks);
