import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
// import Jumbotron from 'react-bootstrap/Jumbotron';
import './BestBooks.css';
import { withAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
import BookItem from './BookItem';
import Form from 'react-bootstrap/Form';

class BestBooks extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      Books: []

    }
  }
  componentDidMount = () => {
    const { user } = this.props.auth0;
    const email = user.email;
    axios.get(`http://localhost:3010/Book?email=${email}`)
      .then(result => {
        this.setState({
          Books: result.data
        })
      }).catch(err => {
        console.log('error');
      })

  }

  addBook = (event) => {
    event.preventDefault();
    const { user } = this.props.auth0;
    const email = user.email;
    const obj = {
      title: event.target.titleBook.value,
      description: event.target.des.value,
      email:email,
      status: event.target.statusBook.value

    }
    axios.post(`http://localhost:3010/addBook`, obj)
      .then(result => {
        this.setState({
          Books: result.data
        })
      }).catch(err => {
        console.log('Error on adding Book');
      })
  }
  render() {
    return (
      <>
        <h1>My Favorite Books</h1>
        <p>
          This is a collection of my favorite books
        </p>
       <button  onClick={this.addBook}> add</button>
        <Form>
          <Form.Group className="mb-3" controlId="titleBook">
            <Form.Label>Title</Form.Label>
            <Form.Control type="text" placeholder="book title"  />
          </Form.Group>
          <Form.Group className="mb-3" controlId="des">
            <Form.Label>Description </Form.Label>
            <Form.Control type="text" placeholder="book description" />

          </Form.Group>
          <Form.Group className="mb-3" controlId="statusBook">
            <Form.Label>Status Book </Form.Label>
            <Form.Control type="text " placeholder="statusBook"  />

          </Form.Group>
        </Form>

        <BookItem 
       Book={this.state.Books}/>

      </>
    )
  }
}

export default withAuth0(BestBooks);
